import React, { useState } from 'react';

// Importa las librerías necesarias

// Componente de verificación en dos pasos
const Verificacion = () => {
    // Estado para almacenar el código ingresado
    const [codigo, setCodigo] = useState('');
    // Estado para controlar si el código es incorrecto
    const [codigoIncorrecto, setCodigoIncorrecto] = useState(false);
    // Estado para controlar si se ha enviado el código
    const [codigoEnviado, setCodigoEnviado] = useState(false);

    // Función para manejar el cambio en el campo de texto
    const handleChangeCodigo = (event) => {
        setCodigo(event.target.value);
    };

    // Función para manejar el envío del código
    const handleSubmitCodigo = (event) => {
        event.preventDefault();

        // Comprueba si el código es correcto
        if (codigo === 'codigoCorrecto') {
            // Código correcto, realiza la acción deseada
            console.log('Código correcto');
        } else {
            // Código incorrecto, muestra el mensaje de error
            setCodigoIncorrecto(true);
        }
    };

    // Función para manejar el reenvío del código
    const handleReenviarCodigo = () => {
        // Lógica para reenviar el código
        console.log('Código reenviado');
    };

    return (
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '100vh', backgroundColor: '#f0f0f0' }}>
            <h1 style={{ marginBottom: '50px' }}>Verificación en dos pasos</h1>
            <p style={{ marginBottom: '30px', textAlign: 'center', width: '300px' }}>Para poder continuar y acceder a la aplicación, por favor, ingrese el código que se le envió a su correo electrónico registrado.</p>
            <form onSubmit={handleSubmitCodigo} style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginBottom: '20px' }}>
                <input type="text" value={codigo} onChange={handleChangeCodigo} style={{ marginBottom: '20px', padding: '10px', width: '200px' }} />
                <button type="submit" style={{ padding: '10px 20px', backgroundColor: '#4CAF50', color: 'white', border: 'none', cursor: 'pointer' }}>Comprobar</button>
            </form>
            {codigoIncorrecto && (
                <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                    <p style={{ color: 'red', marginBottom: '20px' }}>Código incorrecto. Por favor, inténtelo nuevamente.</p>
                    <button onClick={handleReenviarCodigo} style={{ padding: '10px 20px', backgroundColor: '#008CBA', color: 'white', border: 'none', cursor: 'pointer' }}>Reenviar código</button>
                </div>
            )}
        </div>
    );
};

export default Verificacion;
